import React from 'react';

import Form from './Form.js';
import Result from './Result.js';
import './App.css';

// const APIkey = 

class App extends React.Component {

  //stany poczatkowe do zmiany
  state =  {
    value: '',
    date:'',
    city:'',
    sunrise:'',
    sunset:'',
    temp: '',
    pressure:'',
    wind:'',
    err: '',
  }

  handleInputChange = (e) => {
     this.setState({
       value: e.target.value
     })
   }

  handleCitySubmit = e => {
    //powstrzymaj odswiezanie strony//
    e.preventDefault();
    const API = `http://api.openweathermap.org/data/2.5/weather?q=${this.state.value}&units=metric&appid=dc35d37db384d42daf570cc6c707a5e8`
    console.log("potwierdzony formularz")
//albo odrzucony - catch albo spelniony -then

    fetch(API)
      .then(response =>{
        if(response.ok){
          return response
        }
        throw Error('Error occurced')
      })
      .then(response => response.json())
      .then(data => {
        const time = new Date().toLocaleString();

        //uzywam funkcji zwracjaacej obiekt
        this.setState(prevState => ({
          err:false,
          date: time,
          city: this.state.value,
          sunrise: data.sys.sunrise,
          sunset: data.sys.sunset,
          temp: data.main.temp,
          pressure: data.main.pressure,
          wind:data.wind,

      }))
    })
      .catch(err => err => {
        this.setState({
          err:true,
          city: this.state.value
        })
      })


  }

  //konstruuje api 



  render() {
    return (
      <div className="App"> Check the weather
      <Form value = {this.state.value} change={this.handleInputChange} submit={this.handleCitySubmit}/>
      <Result/>
      </div>
    );
  }
}




export default App;
