import React from 'react';

import Form from './Form.js';
import Result from './Result.js';
import './App.css';

class App extends React.Component {

  //stany poczatkowe do zmiany
  state =  {
    value: '',
    date:'',
    city:'',
    sunrise:'',
    sunset:'',
    temp: '',
    pressure:'',
    wind:'',
    err: '',
  }

  handleInputChange = (e) => {
     this.setState({
       value: e.target.value
     })
   }

  handleCitySubmit = e => {
    //powstrzymaj odswiezanie strony//
    e.preventDefault();
    const API = `https://openweathermap.org/data/2.5/find?q=${this.state.value}&units=metric&appid=dc35d37db384d42daf570cc6c707a5e8`
    console.log("potwierdzony formularz")
//albo odrzucony - catch albo spelniony -then

    fetch(API)
      .then(response => console.log(response))
      .catch(err => console.log(err))


  }

  //konstruuje api 



  render() {
    return (
      <div className="App"> Check the weather
      <Form value = {this.state.value} change={this.handleInputChange} submit={this.handleCitySubmit}/>
      <Result/>
      </div>
    );
  }
}




export default App;
